        int saveCount;

        if (!dirtyOpaque) {
            drawBackground(canvas);
        }
        ...
        // Step 3, draw the content
        if (!dirtyOpaque) onDraw(canvas);

        // Step 4, draw the children
        dispatchDraw(canvas);
        ...
        // Step 6, draw decorations (foreground, scrollbars)
        onDrawForeground(canvas);
